mapper.update:
UPDATE iteminfo
SET fileName     = #{fileName},
    absolutePath = #{absolutePath}, serverPath = #{serverPath},
    folder       = #{folder}, parentId = #{parentId}, status = #{status},
    sha1         = #{sha1}, updateDate = #{updateDate}, itemTypeId = #{itemTypeId}
WHERE itemId = #{itemId}

UPDATE iteminfo
SET status = '不可用'
WHERE itemId = #{itemId}

mapper.select:
SELECT * FROM iteminfo
WHERE 1=1

    and itemId = #{itemId}


    and fileName = #{fileName}


    and absolutePath = #{absolutePath}


    and serverPath = #{serverPath}


    and folder = #{folder}


    and parentId = #{parentId}


    and status = #{status}
