mapper.update:
UPDATE hander
SET memberId = #{memberId}, itemId = #{itemId},
    parentId = #{parentId},
    fileName = #{fileName}, folder = #{folder}, fullPath = #{fullPath},
    status   = #{status}
WHERE handerId = #{handerId}

UPDATE hander
SET status = '不可用'
WHERE

    handerId = #{handerId}


    memberId = #{memberId} AND itemId = #{itemId}

mapper.delete:
DELETE FROM hander
WHERE

    handerId = #{handerId}


    memberId = #{memberId} AND itemId = #{itemId}

mapper.select:
SELECT * FROM hander
WHERE 1=1

    and handerId = #{handerId}


    and memberId = #{memberId}


    and itemId = #{itemId}


    and parentId = #{parentId}


    and fileName = #{fileName}


    and folder = #{folder}


    and fullPath = #{fullPath}


    and status = #{status}
