mapper.delete:
DELETE FROM hander
WHERE memberId = #{memberId} AND

    itemId = #{item.itemId}

mapper.update:
UPDATE member
SET status = '不可用'
WHERE memberId = #{memberId}

mapper.select:
SELECT i.*
FROM member m, iteminfo i, hander mi
WHERE m.memberId = mi.memberId AND i.itemId = mi.itemId AND mi.memberId = #{memberId}
AND i.status = '正常'

    and i.parentId = #{items.parentId}


    and i.parentId = -1

SELECT * FROM member
WHERE 1=1

    and username = #{username}


    and name = #{name}


    and status = #{status}
